create extension if not exists "uuid-ossp";
create database main_task4

CREATE TABLE products (
	id uuid PRIMARY KEY DEFAULT uuid_generate_v4(),
	title - text, NOT NULL
	description - text
	image - text
	price - integer
)

CREATE TABLE stocks (
	id uuid PRIMARY KEY DEFAULT uuid_generate_v4(),
	stock_id uuid,
	count integer,
	foreign key ("stock_id") references "products" ("id")
)



	
